con:config.script:
import se.skl.rivta.testsupport.soapui.datasource.XmlDataReader
import com.eviware.soapui.support.GroovyUtils

def utils = new GroovyUtils(context)
def dataFile = new File(utils.projectPath + "/data.xml")
def source = new XmlDataReader(context, dataFile)

source.load(testRunner.testCase.getName())

con:configuration.scriptText:
def content = context.httpResponse.responseContent content = content.replaceAll( "<!\\[CDATA\\[", "" ) content = content.replaceAll( "]]>", "" )
//log.info( content )
context.httpResponse.responseContent = content

import com.eviware.soapui.support.XmlHolder


def holder = new XmlHolder( messageExchange.responseContentAsXml )

holder.namespaces["req"] = "RequestDataNS"
def requestDataNode = holder.getNodeValue("//req:requestdata[1]/req:data[1]")
if (requestDataNode != null) {
	if (context.httpResponse) {
		context.httpResponse.responseContent = requestDataNode
	}
}

import javax.xml.XMLConstants
import javax.xml.transform.stream.StreamSource
import javax.xml.validation.SchemaFactory
import com.eviware.soapui.support.XmlHolder

def samlRequest = new XmlHolder(messageExchange.responseContentAsXml);
samlRequest.namespaces["saml2"] = "urn:oasis:names:tc:SAML:2.0:assertion"
def saml_assertions =  samlRequest.getNodeValues("//saml2:Assertion");

// Specify an XSD Schema
def xsdFilePath = "http://docs.oasis-open.org/security/saml/v2.0/saml-schema-protocol-2.0.xsd"

// Create validation objects
def factory = SchemaFactory.newInstance(XMLConstants.W3C_XML_SCHEMA_NS_URI);
def schema = factory.newSchema(new StreamSource(xsdFilePath));
def validator = schema.newValidator();

saml_assertions.each{ assertion ->
	// Validate the response against the schema
	assertionStr = assertion.toString();
	log.info " assertion: " + assertionStr
	assert validator.validate(new StreamSource(new StringReader(assertion))) == null;
}
