import se.skl.rivta.testsupport.soapui.datasource.XmlDataReader
import com.eviware.soapui.support.GroovyUtils

def utils = new GroovyUtils(context)
def dataFile = new File(utils.projectPath + "/data.xml")
def source = new XmlDataReader(context, dataFile)

source.load(testRunner.testCase.getName())

con:configuration.scriptText:
import se.skl.rivta.tksupport.soapui.ContentAssertions
def ca = new ContentAssertions(context, messageExchange, log)
ca.assertSpecificValueInAllRecords('/urn:careDocumentation/hdurn2:careDocumentationHeader/hdurn2:patientId/hdurn2:id', context.patientId)

def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
def httpResponseHeaders = messageExchange.getResponseHeaders() 

for(header in httpResponseHeaders.keySet())
{
	if(header.equalsIgnoreCase("x-skltp-prt"))
		x_skltp_prt = header
	if(header.equalsIgnoreCase("content-length"))
		content_length = header
	if(header.equalsIgnoreCase("content-type"))
		content_type = header
	if(header.equalsIgnoreCase("x-skltp-correlation-id"))
		x_skltp_correlation_id = header
}
assert !x_skltp_prt.isEmpty()

// Common asserts
assert httpResponseHeaders.keySet().contains("Server")
assert httpResponseHeaders.keySet().contains("Date")

// Check content
// Implementation dependent asserts
if (!content_length.isEmpty()) {
	assert httpResponseHeaders.keySet().contains("#status#")
	assert httpResponseHeaders["#status#"].contains("HTTP/1.1 200 OK")
	assert httpResponseHeaders[content_type].contains("text/xml; charset=UTF-8")
	assert httpResponseHeaders[x_skltp_correlation_id] != ""
}
// Other
else {
	assert false
}

