import se.skl.rivta.testsupport.soapui.datasource.XmlDataReader 
import com.eviware.soapui.support.GroovyUtils  

def utils = new GroovyUtils(context) 

//Create the datafile with global data 
def dataFile = new File(utils.projectPath + "/data.xml") 
def source = new XmlDataReader(context, dataFile)  

def alert = com.eviware.soapui.support.UISupport;
//Define a file pointer for groovy to handle the file operations.
def inputFile = new File(utils.projectPath + "/data.xml") 


if(!inputFile.exists())
{
    //Display an alert if the file is not found.
    alert.showInfoMessage("Input File " inputFile " not found!");   
}
else
{
    //Read and parse XML file and store it into a variable
    def InputXML = new XmlParser().parseText(inputFile.text)

    //Find/ Filter XML nodes based on a condition   
    def InputRow = InputXML.globaldata.findAll{
        it.VP.text();  

        //log.info(it.VP.text());
        def url = it.VP.text() 

        //Set environent variable
        testRunner.testCase.testSuite.setPropertyValue("ENV", url)
    }
}

def url = testRunner.testCase.testSuite.getPropertyValue("ENV")

//only run in dev
if ((url.contains("qa") || url.contains("test")))
testRunner.cancel( "Feature not implemented in "+ url + ", cancelling test step" )   

/* only run in dev and test
if ((url.contains("qa")))
	testRunner.cancel( "Feature not implemented in "+ url + ", cancelling test step" )   */

def url = testRunner.testCase.testSuite.getPropertyValue("ENV")

//only run in dev
if ((url.contains("qa") || url.contains("test")))
testRunner.cancel( "Feature not implemented in "+ url + ", cancelling test step" )   

/* only run in dev and test
if ((url.contains("qa")))
	testRunner.cancel( "Feature not implemented in "+ url + ", cancelling test step" )   */

