def utils = new GroovyUtils(context)
def dataFile = new File(utils.projectPath + "/data.xml")
def source = new XmlDataReader(context, dataFile)

source.load(testRunner.testCase.getName())

con:configuration.scriptText:
def getCurrentProject() {
	return context.testCase.testSuite.project
}

// Hämta det aktuella interfacet utifrån namn
def getInterface(project, interfaceName) {
	for (iface in project.interfaceList) {
		if (iface.name.equals(interfaceName)) {
			return iface
		}
	}
	assert false : "Interface " + interfaceName + " hittades inte"
}

// Hämta interfacets cachade WSDL (den som sparas i projektfilen)
def getWsdlDefinitionCacheContent(iface) {
	assert iface.isCached() : "Cachad WSDL saknas"
	for (definitionPartConfig in iface.getConfig().getDefinitionCache().getPartList()) {
		def wsdlType = "http://schemas.xmlsoap.org/wsdl/"
		def type = definitionPartConfig.getType()
		if (type.equals(wsdlType)) {
			def content = definitionPartConfig.getContent().toString()
			def wsdl = new XmlSlurper(true,true,false).parseText(content)
			return wsdl
		}
	}
	assert false : "WSDL cache content hittades inte"
}

// Hämta interfacets cachade WSDL (den som sparas i projektfilen)
def getCachedWsdl(interfaceName) {
	def project = getCurrentProject()
	def iface = getInterface(project, interfaceName)
	def wsdl = getWsdlDefinitionCacheContent(iface).toString()
	return wsdl
}

// XmlParser => String
// Ursprunglig formatering och kommentarer behålls inte
def xmlParserToXmlString(xmlParser) {
	def stringWriter = new StringWriter()
	def nodePrinter = new XmlNodePrinter(new PrintWriter(stringWriter))
	nodePrinter.setPreserveWhitespace(false)
	nodePrinter.print(xmlParser)
	def xmlString = stringWriter.toString()

	return xmlString.trim()
}

// Ersätter attribut som inte ska jämföras med dummyvärden
def replaceXmlContentNotToBeCompared(xmlParser) {
	def replacementString =  "THIS_VALUE_IS_REPLACED_BEFORE_COMPARISION"	
	xmlParser."wsdl:types"."xs:schema"."xs:import".each {
		it.attributes().schemaLocation = replacementString
	}	
	xmlParser."wsdl:service"."wsdl:port"."soap:address".each {
		it.attributes().location = replacementString
	}	
}

def fixDocumentationElement(wsdlXml) {
	if (wsdlXml.contains("<wsdl:documentation>")) {
		wsdlXml = wsdlXml
		.replace("<wsdl:documentation>", "<xs:annotation xmlns:xs=\"http://www.w3.org/2001/XMLSchema\"><xs:documentation>")
		.replace("</wsdl:documentation>", "</xs:documentation></xs:annotation>")
	}
	return wsdlXml
}

// Tar bort kommentarer och formattering + gör vissa justeringar för att kunna jämföra
def getComparableWsdl(wsdlXml) {
	wsdlXml = fixDocumentationElement(wsdlXml)
	def xmlParser = new XmlParser().parseText(wsdlXml)
	replaceXmlContentNotToBeCompared(xmlParser)
	return xmlParserToXmlString(xmlParser)
}

def testedInterfaceName = context.getProperty( "testedInterfaceName")

// WSDL från reply
def retreivedWsdl = messageExchange.getResponseContent()

// Cachad WSDL från projektfilen
def cachedWsdl = getCachedWsdl(testedInterfaceName)

// Här ska båda strängarna vara lika förutom:
// - Formatering
// - xs:import schemaLocation, wsdl från projektfilen pekar på en lokal xsd-fil istället för den "riktiga"
// - soap:address location pekar också fel

def cleanedRetreivedWsdl = getComparableWsdl(retreivedWsdl)
def cleanedCachedWsdl = getComparableWsdl(cachedWsdl)

// Här ska båda vara lika
def isWsdlEqual = cleanedRetreivedWsdl.equals(cleanedCachedWsdl)
if (!isWsdlEqual) {
	log.info "Hämtad wsdl överensstämmer inte med den förväntade!"
	log.info "Hämtad (rensad) wsdl: " + cleanedRetreivedWsdl
	log.info "Lokal (rensad) wsdl: " + cleanedCachedWsdl
	assert false : "Hämtad wsdl överensstämmer inte med den förväntade!"
}

// Extra koll så att inte något gått snett vid parsningen och båda strängarna är tomma eller liknande
assert cleanedRetreivedWsdl.startsWith("<wsdl:definitions") : "Oväntad start på WSDL"
assert cleanedRetreivedWsdl.endsWith("</wsdl:definitions>") : "Oväntat slut på WSDL"

con:config.script:
import  com.eviware.soapui.support.types.StringToObjectMap
import se.skl.rivta.testsupport.soapui.datasource.XmlDataReader
import com.eviware.soapui.support.GroovyUtils

def loadPropertiesFromFile() {
	def utils = new GroovyUtils(context)
	def dataFile = new File(utils.projectPath + "/data.xml")
	def source = new XmlDataReader(context, dataFile)
	source.load(testRunner.testCase.getName())
}

def getOtherTestcase(testcaseName) {
	def testcase = context.testCase.testSuite.getTestCaseByName(testcaseName)
	assert testcase != null : String.format("Testfallet %s hittades inte", testcaseName)
	return testcase
}

// Kör ett annat testfall (i samma svit) med givna properties
def runOtherTestcase(testcaseName, properties) {
	def testcase = getOtherTestcase(testcaseName) 
	def runner = testcase.run(properties, false)

	if (runner.getStatus() != com.eviware.soapui.model.testsuite.TestRunner.Status.FINISHED) {
		// Återrapportera felet i anropat testfall
		// (Beroende på fel så blir det tyvärr ändå inte alltid ett tydligt felmeddelande.)
		def errorMessage = String.format("Anropat testfall %s misslyckades, felet är: %s - %s", testcaseName, runner.getReason(), runner.getError().toString())
		assert false : errorMessage
	}
}

// Testa wsdl med angiven endpoint och interface
def runGetWsdlTestcase(endpoint, interfaceName) {
	def testcaseName = "Hämta wsdl - deltest"
	def properties = new StringToObjectMap()
	properties.put("wsdlEndpoint", endpoint)
	properties.put("testedInterfaceName", interfaceName)
	log.info String.format("Kör %s med endpoint %s och interface %s", testcaseName, endpoint, interfaceName)
	runOtherTestcase(testcaseName, properties)
}

// Hämta parametrar
loadPropertiesFromFile()
def delimiter = ";"
def endpoints = context.getProperty( "wsdlEndpoints").split(delimiter)
def interfaces = context.getProperty( "interfaces").split(delimiter)
assert endpoints.length == interfaces.length : "Felaktiga parametrar i datafil"

// Testa alla interface
for (int parameterIndex = 0; parameterIndex < endpoints.length; parameterIndex++) {
	def endpoint =  endpoints[parameterIndex].trim()
	def interfaceName = interfaces[parameterIndex].trim()	
	runGetWsdlTestcase(endpoint, interfaceName) 
}

