con:mockOperation.con:dispatchPath:
import com.eviware.soapui.support.XmlHolder
import com.eviware.soapui.impl.wsdl.support.wsdl.WsdlContext
import com.eviware.soapui.impl.wsdl.support.wsdl.WsdlValidator
import com.eviware.soapui.impl.wsdl.panels.mockoperation.WsdlMockRequestMessageExchange

synchronized(mockRequest.context.mockService){
def mockService = mockOperation.mockService

def wsdlcontext = context.mockService.getMockedInterfaces()[0].getDefinitionContext();
def validator = new WsdlValidator(wsdlcontext);
def msgExchange = new WsdlMockRequestMessageExchange(mockRequest, mockOperation); 
def errors = validator.assertRequest(msgExchange, false);

def msg = ""
if (errors.length > 0) {
	errors.each {
		log.error("Incoming request contains error ${it.toString()}")
		msg += "${it.toString()}\n"
	}
	context.validationMessages = msg
	return "SoapFault-Request"
}

def holder = new XmlHolder( mockRequest.requestContent )
holder.namespaces['soapenv'] = 'http://schemas.xmlsoap.org/soap/envelope/'
holder.namespaces['urn'] = 'urn:riv:itintegration:registry:1'
holder.namespaces['urn1'] = 'urn:riv:clinicalprocess:healthcond:description:GetCareDocumentationResponder:2'
holder.namespaces['urn2'] = 'urn:riv:clinicalprocess:healthcond:description:2'

def patientId = holder.getNodeValue('/soapenv:Envelope/soapenv:Body/urn1:GetCareDocumentation/urn1:patientId/urn2:id')
def patientIdType = holder.getNodeValue('/soapenv:Envelope/soapenv:Body/urn1:GetCareDocumentation/urn1:patientId/urn2:type')
def careUnitHSAid = holder.getNodeValue('/soapenv:Envelope/soapenv:Body/urn1:GetCareDocumentation/urn1:careUnitHSAid')
def sourceSystemHSAid = holder.getNodeValue('/soapenv:Envelope/soapenv:Body/urn1:GetCareDocumentation/urn1:sourceSystemHSAid')
def careContactId = holder.getNodeValue('/soapenv:Envelope/soapenv:Body/urn1:GetCareDocumentation/urn1:careContactId')

context.patientId = patientId 
context.patientIdType = patientIdType
context.careUnitHSAid = careUnitHSAid ?: "CARE_UNIT_HSA"
context.sourceSystemHSAid = sourceSystemHSAid ?: "SOURCE_SYSTEM_HSA"
context.careContactId = careContactId ?: "CARE_CONTACT_ID"


// Ursprunglig testfallsbeskrivning:
// https://skl-tp.atlassian.net/wiki/spaces/SKLTP/pages/20774964/VP+-+Tester+ursprunglig+avs+ndare#VP-Testerursprungligavs%C3%A4ndare-Testfall%233:VPextraherarinformationomavs%C3%A4ndarefr%C3%A5nbifogadx-vp-sender-id

// -----------------------------------------------------------------------------
// Testerna är gjorda för att passera NTjP som är SUT'en, men en mock behövs bortanför för att ta emot och verifiera förändringarna.
// -----------------------------------------------------------------------------

// Check if required headers are included
if (!mockRequest.requestHeaders['x-rivta-original-serviceconsumer-hsaid']) {
	log.info("Missing x-rivta-original-serviceconsumer-hsaid")
	return "SoapException"
}
if (!mockRequest.requestHeaders['x-vp-sender-id']) {
	log.info("Missing x-vp-sender-id")
	return "SoapException"
}



// Compare against expected headers
def expectedOriginalServiceConsumerHsaId = context.expand('${#Project#HSA_KONSUMENT1}')
def requestOriginalServiceConsumerHsaId = mockRequest.requestHeaders['x-rivta-original-serviceconsumer-hsaid'][0]
if (expectedOriginalServiceConsumerHsaId != requestOriginalServiceConsumerHsaId) {
	log.info("${expectedOriginalServiceConsumerHsaId}, ${requestOriginalServiceConsumerHsaId}")
	return "SoapException"
}

def expectedSenderId = context.expand('${#Project#HSA_NTJP}')
def requestSenderId = mockRequest.requestHeaders['x-vp-sender-id'][0]
if (expectedSenderId != requestSenderId) {
	log.info("${expectedSenderId}, ${requestSenderId}")
	return "SoapException"
}



def responseName = mockRequest.requestHeaders['x-mock-response'][0]
log.debug("responseName: ${responseName}")

return responseName
}
